import React from 'react'
import { Spinner, Card, CardBody, CardImg, CardTitle, CardText, CardSubtitle, Button } from 'reactstrap'
import { useTranslation } from 'react-i18next';
import LogoProject from '../../logo_project.png';
import LogoUniv from '../../logo_univ.png';
import LogosCardImg from '../../logo_card_img.png'


export default function AboutPage() {
    const { t, i18n } = useTranslation();

    return (
        <div>
            <Card>
                <CardBody>
                    <img src={LogoUniv} alt="University logo" className="univ-logo"></img>
                    <img src={LogoProject} alt="Project logo" classname="project-logo"></img>
                    <CardImg top width="100%" src="/assets/318x180.svg" alt="Card image cap" />
                    <CardTitle>{t('About.2')}</CardTitle>                             
                    <CardText>{t('About.3')}</CardText>
                </CardBody>
            </Card>
        </div>
    )
}
